$img_array = explode('+++', $img);
$img_string = '';
foreach ($img_array as $value) {
	$img_string = $img_string . stristr($value, '>', true) . '>+++';
}
$img_string = substr($img_string, 0, -3);
$image_aray = explode('+++', $img_string);
foreach ($image_aray as $value) {
	$smallvalue = str_replace('authors/', 'authors/small', $value);
	$smallvalueclass = str_replace('src="/images/authors', 'class="smallimg" src="/images/authors', $smallvalue);
	$smallvalueclass = str_replace('style="height:500px; width:800px"', '', $smallvalueclass);
	$valueclass = str_replace('src="/images/authors', 'class="bigimg" src="/images/authors', $value);
	$valueclass = str_replace('style="height:500px; width:800px"', '', $valueclass);
	$smallvaluevalueclass = $valueclass . $smallvalueclass;
	$text = str_replace($value, $smallvaluevalueclass, $text);
}
echo $text;
